import React, { useContext, useEffect, useState } from 'react';
import Echo from 'laravel-echo';
import Pusher from 'pusher-js';
import {UserContext} from "../UserContext";
import axios from 'axios';

const Chat = () => {
    const [chatRooms, setChatRooms] = useState([]);
    const {user, getUser} = useContext(UserContext);
    const [selectedChatRoom, setSelectedChatRoom] = useState(null);
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            window.Pusher = Pusher;
            window.Echo = new Echo({
                broadcaster: 'pusher',
                key: 'your-pusher-app-0abcc2e1499f2ddde046',
                cluster: 'your-pusher-app-cluster',
                forceTLS: true,
            });

            const fetchChatRooms = async () => {
                const response = await axios.get(`/chats?user_id=${user.data.id}`);
                setChatRooms(response.data);
            };

            fetchChatRooms();
        }
    }, [user]);

    useEffect(() => {
        if (selectedChatRoom) {
            window.Echo.channel(`chat.${selectedChatRoom.chat_id}`)
                .listen('MessageSent', (e) => {
                    console.log('Message received:', e);  // Add console log to check messages
                    setMessages(messages => [...messages, e.message]);
                });

            return () => {
                window.Echo.leaveChannel(`chat.${selectedChatRoom.chat_id}`);
            };
        }
    }, [selectedChatRoom]);

    const selectChatRoom = async (chatRoom) => {
        setSelectedChatRoom(chatRoom);
        const response = await axios.get(`/chats/${chatRoom.chat_id}`);
        setMessages(response.data.messages);
    };

    const sendMessage = async () => {
        if (selectedChatRoom && message) {
            await axios.post(`/chats/${selectedChatRoom.chat_id}`, {
                chat_id: selectedChatRoom.chat_id,
                message_text: '123',
                sender_id: user.data.id,
                message_datetime: "2024-06-10 22:22:22"
            });
            setMessage('');
        }
    };

    return (
        <div>
            <div>
                <h2>Chat Rooms</h2>
                <ul>
                    {chatRooms.map(chatRoom => (
                        <li key={chatRoom.chat_id} onClick={() => selectChatRoom(chatRoom)}>
                            {chatRoom.chat_id}
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                {selectedChatRoom && (
                    <>
                        <h3>Messages in {selectedChatRoom.chat_id}</h3>
                        <div>
                            {messages.map((msg) => (
                                <div key={msg.message_id}>{msg.message_text}</div>
                            ))}
                        </div>
                        <input
                            type="text"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                        />
                        <button onClick={sendMessage}>Send</button>
                    </>
                )}
            </div>
        </div>
    );
};

export default Chat;
