import Navbar from "../components/Navbar";
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import Footer from "../components/Footer";
import Paginator from "../components/Paginator";

export default function IndexPage() {
    const params = useParams();
    const [products, setProducts] = useState([]);
    const [pagesCount, setPagesCount] = useState(0);
    const [baseLink, setBaseLink] = useState("http://localhost:3000");

    useEffect(() => {
        let query = "";
        if (params.category_id) {
            setBaseLink();
            query = `?category_id=${params.category_id}`;
        }
        axios.get(`/products/all${query}`)
            .then(res => {
                setProducts(res.data.data);
                setPagesCount(res.data.pages_count);
            })
    }, [params]);

    return (
        <div>
            <div className="bg-gray-100">
                {params.category_id && (
                    <Navbar selected_category_id={params.category_id}/>
                )}
                {!params.category_id && (
                    <Navbar/>
                )}

                {products.length
                    ?
                    <>
                        <div className="flex justify-center items-center mt-2">
                            <Paginator/>
                        </div>
                        <div className="max-w-screen-2xl mx-auto p-5 sm:p-10 md:p-16 mb-24">
                            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-10">
                                {products.map((product) =>
                                    <ProductCard product={product}
                                                 linkToDisplayPage={`/product-details/${product.id}`}/>
                                )}
                            </div>
                        </div>
                        <div className="flex justify-center items-center mb-2 -mt-16">
                            <Paginator/>
                        </div>
                    </>
                    :
                    <h1 className="h-screen text-center text-4xl mt-32 -mb-64">Товары не найдены, измените параметры поиска</h1>
                }

                <Footer />
            </div>
        </div>
    );
}
