import { useContext, useEffect } from "react";
import { useState } from "react";
import { UserContext } from "../UserContext";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io('http://localhost:8003');

export default function ChatsIndexPage() {
    const {user, getUser} = useContext(UserContext);
    const [chats, setChats] = useState([]);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState(''); 

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            fetchChats();
            socket.on('chat', handleChatEvent);
            socket.on('message', handleMessageEvent);
            return () => {
                socket.off('chat', handleChatEvent);
                socket.off('message', handleMessageEvent);
            };
        }
    }, [user]);

    const handleChatEvent = (data) => {
        const { action, chat } = data;
        if (action === 'create') {
          setChats(prevChats => [...prevChats, chat]);
        }
    };

    const handleMessageEvent = (data) => {
        const { action, message } = data;
        if (action === 'create') {
          setMessages(prevMessages => ({
            ...prevMessages,
            [message.chat_id]: [...(prevMessages[message.chat_id] || []), message]
          }));
        }
    };

    const fetchChats = () => {
        axios.get(`http://localhost:8003/api/chats?user_id=${user.data.id}`)
          .then(response => {
            setChats(response.data);
          })
          .catch(error => {
            console.error('Error fetching chats:', error);
          });
    };

    const fetchMessages = (chatId) => {
        axios.get(`http://localhost:8003/api/chats/${chatId}/messages`)
          .then(response => {
            setMessages(response.data);
            socket.emit('join', chatId);
          })
          .catch(error => {
            console.error(`Error fetching messages for chat ${chatId}:`, error);
          });
    };


    const sendMessage = (chatId) => {
        axios.post(`http://localhost:8003/api/chats/${chatId}/messages`, {
             message_text: newMessage,
             message_datetime: '2024-06-10 20:20:20',
             sender_id: user.data.id 
          })
          .then(() => {
            setNewMessage('');
          })
          .catch(error => {
            console.error('Error sending message:', error);
          });
    };

    return (
        <div className="container mx-auto p-4">
          <h1 className="text-2xl font-bold mb-4">Chat App</h1>
          <div className="flex">
            <div className="w-1/3">
              <h2 className="text-xl font-bold mb-2">Chats</h2>
              <ul className="border p-2">
                {chats.map(chat => (
                  <li key={chat.chat_id} className="mb-2">
                    {chat.chat_id}
                    <button onClick={() => fetchMessages(chat.chat_id)} className="ml-2 text-blue-500">Load Messages</button>
                  </li>
                ))}
              </ul>
            </div>
            <div className="w-2/3 ml-4">
              <h2 className="text-xl font-bold mb-2">Messages</h2>
              {messages.map( (message) => (
                <ul>
                    <div key={message.chat_id} className="border p-2 mb-4">
                        <li key={message.message_id}>
                            {message.message_text} <span className="text-gray-500">{message.message_datetime}</span>
                        </li>
                    <div className="mt-2">
                        <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        className="border p-2 mr-2"
                        />
                        <button onClick={() => sendMessage(message.chat_id)} className="bg-green-500 text-white p-2 rounded">Send</button>
                    </div>
                    </div>
                </ul>
              ))}
            </div>
          </div>
        </div>
    );
}
