import Image from "./Image";

export default function ProductImg({product, index = 0, className = null}) {
    if (!product.photos?.length) {
        return '';
    }
    if (!className) {
        className = 'w-4/5';
    }
    return (
        <Image className={className} src={product.photos[index]} alt="" />
    )
}
