import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ProfileSellingBetCard from "./ProfileSellingBetCard";
import Footer from "../../Helpers/Footer";
import Paginator from "../../Helpers/Paginator";

export default function ProfileSellingBets() {
    const params = useParams();
    const [baseLink, setBaseLink] = useState("");
    const [pagesCount, setPagesCount] = useState(0);
    const [bets, setBets] = useState([]);

    useEffect(() => {
        let product_id = params.action;
        setBaseLink(`http://localhost:3000/profile/selling/${product_id}/bet`);
        if (product_id) {
            axios.get(`/bets/list-product-bets/${product_id}`)
                .then(res => {
                    setPagesCount(res.data.pagesCount);
                    setBets(res.data.betsData.bets);
                });
        }
    }, [params]);

    if (bets) {
        return (
            <div>
                {bets.length 
                    ?
                    <>
                        <div className="flex flex-col">
                            <div className="flex flex-wrap items-center mb-4">
                                <input type="date" placeholder="From Date" className="border p-2 mr-2"/>
                                <input type="date" placeholder="To Date" className="border p-2 mr-2"/>
                                <select className="border p-2 mr-2">
                                    <option value="">User Rating</option>
                                    <option value="asc">Ascending</option>
                                    <option value="desc">Descending</option>
                                </select>
                                <select className="border p-2 mr-2"

                                >
                                <option value="">Status</option>
                                <option value="accepted">Accepted</option>
                                <option value="declined">Declined</option>
                                <option value="pending">Pending</option>
                                </select>
                            </div>
                        </div>

                        <div className="flex justify-center items-center mt-2 mb-4">
                            <Paginator pagesCount={pagesCount} baseLink={baseLink}/>
                        </div>
                        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4">
                            {bets.map((bet) => (
                                <ProfileSellingBetCard bet={bet}/>
                        ))}
                        </div>
                        <div className="flex justify-center items-center mt-4">
                            <Paginator pagesCount={pagesCount} baseLink={baseLink}/>
                        </div>
                    </> 
                    :
                    <>
                    </>
                }

                {bets.length 
                    ?
                    <div className="mt-24">
                        <Footer />
                    </div>
                    :
                    <div>
                        <h2 className="text-center text-2xl">Ставки не найдены</h2>
                        <br/>
                        <br/>
                        <div className="mt-96">
                            <Footer />
                        </div>    
                    </div>
                }
            </div>
        );
    }
}
