import { useContext, useEffect } from "react";
import { useState } from "react";
import { UserContext } from "../UserContext";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io('http://localhost:8003');

export default function ChatsIndexPage() {
    const {user, getUser} = useContext(UserContext);
    const [chats, setChats] = useState([]);
    const [selectedChatId, setSelectedChatId] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState(''); 

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            fetchChats();
            socket.on('chat', handleChatEvent);
            socket.on('message', handleMessageEvent);
            return () => {
                socket.off('chat', handleChatEvent);
                socket.off('message', handleMessageEvent);
            };
        }
    }, [user]);

    const handleChatEvent = (data) => {
        const { action, chat } = data;
        if (action === 'create') {
          setChats(prevChats => [...prevChats, chat]);
        }
    };

    const handleMessageEvent = (data) => {
        const { action, message } = data;
        if (action === 'create' && message.chat_id == selectedChatId) {
          setMessages(prevMessages => [...prevMessages, message]);
        }
    };

    const fetchChats = () => {
        axios.get(`http://localhost:8003/api/chats?user_id=${user.data.id}`)
          .then(response => {
            setChats(response.data);
          })
          .catch(error => {
            console.error('Error fetching chats:', error);
          });
    };

    const fetchMessages = (chatId) => {
        setSelectedChatId(chatId);
        axios.get(`http://localhost:8003/api/chats/${chatId}/messages`)
          .then(response => {
            setMessages(response.data);
            socket.emit('join', chatId);
          })
          .catch(error => {
            console.error(`Error fetching messages for chat ${chatId}:`, error);
        });
    };


    const sendMessage = (chatId) => {
        axios.post(`http://localhost:8003/api/chats/${chatId}/messages`, {
             message_text: newMessage,
             message_datetime: '2024-06-10 20:20:20',
             sender_id: user.data.id 
          })
          .then(() => {
            setNewMessage('');
          })
          .catch(error => {
            console.error('Error sending message:', error);
          });
    };

    return (
        <div className="container mx-auto p-4 flex">
          <div className="w-1/4 border-r pr-4">
            <h1 className="text-2xl font-bold mb-4">Chats</h1>
            <ul className="border p-2">
              {chats.map(chat => (
                <li key={chat.chat_id} className={`mb-2 p-2 cursor-pointer ${selectedChatId === chat.chat_id ? 'bg-blue-100' : ''}`} onClick={() => {
                  setSelectedChatId(chat.chat_id);
                  fetchMessages(chat.chat_id);
                }}>
                  {chat.chat_id}
                </li>
              ))}
            </ul>
          </div>
          <div className="w-3/4 pl-4">
            {selectedChatId ? (
              <div>
                <h2 className="text-xl font-bold mb-2">Messages</h2>
                <ul className="border p-2 h-80 overflow-y-scroll">
                  {(messages).map(message => (
                    <li key={message.message_id} className="mb-2">
                      {message.message_text} 
                      <span className="text-gray-500">{message.message_datetime}</span>
                    </li>
                  ))}
                </ul>
                <div className="mt-2 flex">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    className="border p-2 mr-2 w-full"
                    placeholder="Type a message"
                  />
                  <button onClick={() => sendMessage(selectedChatId)} className="bg-green-500 text-white p-2 rounded">Send</button>
                </div>
              </div>
            ) : (
              <div className="text-center text-gray-500">Select a chat to view messages</div>
            )}
          </div>
        </div>
    );
}
