import axios from "axios";
import { useContext, useState } from "react";
import { useEffect } from "react";
import { UserContext } from "../../../UserContext";



export default function ProfileBuyings() {
    const { user, getUser } = useContext(UserContext);
    const [bets, setBets] = useState([]);

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            axios.get(`/bets/user-products-bets?user_id=${user.data.id}`)
                .then(res => {
                    setBets(res.data);
                });
        }
    }, []);

    return (
        <div>
             {!bets.length
                ?
                <>
                    <div className="flex justify-center items-center mt-2">
                        <Paginator pagesCount={pagesCount} baseLink={baseLink}/>
                    </div>
                    <div className="max-w-screen-2xl mx-auto p-5 sm:p-10 md:p-16 mb-24">
                        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-5 gap-10">
                            {products.map((product) =>
                                <ProductCard product={product} linkToDisplayPage={linkToDisplayPage + product.id}
                                    ableToDelete={ableToDelete}/>
                            )}
                        </div>
                    </div>
                    <div className="flex justify-center items-center mb-2 -mt-16">
                        <Paginator pagesCount={pagesCount} baseLink={baseLink}/>
                    </div>
                </>
                :
                <h1 className="h-screen text-center text-4xl mt-32 -mb-64">Вы не делали ставки на товары</h1>
            }
        </div>
    );
}
