import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { UserContext } from "../../../UserContext";
import { useParams } from "react-router-dom";

export default function DealDetails() {
    const {user, getUser} = useContext(UserContext);
    const [deal, setDeal] = useState(null);
    const [cards, setCards] = useState([]);
    const params = useParams();

    useEffect(() => {
        if (!user) {
            getUser();  
        } else {
            axios.get(`/deals/show-deal/${params.name}`)
            .then(res => {
                setDeal(res.data);
                axios.get(`/user/credit-cards?user_id=${user.data.id}`)
                .then(res => {
                    setCards(res.data.credit_cards);
                })
            })
        }
    }, [params]);

    const [creditCard, setCreditCard] = useState('');
    const [confirmCode1, setConfirmCode1] = useState('');
    const [confirmCode2, setConfirmCode2] = useState('');

    const handleCreditCardChange = (e) => setCreditCard(e.target.value);
    const handleConfirmCode1Change = (e) => setConfirmCode1(e.target.value);
    const handleConfirmCode2Change = (e) => setConfirmCode2(e.target.value);

    const handleConfirm = () => alert('Deal confirmed!');
    const handleFail = () => alert('Deal failed!');

    if (!deal) {

    }

    return (
        <div className="min-h-screen bg-cyan-50 flex flex-col items-center p-6">
          <div className="w-full md:w-3/4 lg:w-2/3 xl:w-1/2 rounded overflow-hidden shadow-lg my-4 bg-white p-8">
            <h2 className="text-3xl font-bold mb-4">{deal.name}</h2>
            <p className="text-gray-700 text-base mb-4">{deal.description}</p>
            <p className="text-gray-700 text-base mb-4"><strong>Продавец:</strong> {deal.bet.product.owner.first_name} {deal.bet.product.owner.second_name}</p>
            <p className="text-gray-700 text-base mb-4"><strong>Покупатель:</strong> {deal.bet.user.first_name} {deal.bet.user.second_name}</p>
            <p className="text-gray-700 text-base mb-4"><strong>Статус сделки:</strong> {deal.deal_status.status_description}</p>
            <p className="text-gray-700 text-base mb-4"><strong>Сумма сделки:</strong> {deal.bet.price} BYN</p>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="credit-card">Выбор кредитной карты для оплаты</label>
              <select
                id="credit-card"
                value={creditCard}
                onChange={handleCreditCardChange}
                className="block appearance-none w-full bg-white border border-gray-400 hover:border-gray-500 px-4 py-2 pr-8 rounded shadow leading-tight focus:outline-none focus:shadow-outline"
              >
                <option value="">Select your credit card</option>
                <option value="visa">Visa</option>
                <option value="mastercard">MasterCard</option>
                <option value="amex">American Express</option>
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="confirm-code-1">Код подтверждения передачи покупателю</label>
              <input
                type="text"
                id="confirm-code-1"
                value={confirmCode1}
                onChange={handleConfirmCode1Change}
                className="w-full px-4 py-2 border border-gray-400 rounded"
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="confirm-code-2">Код подтверждения передачи продавцу</label>
              <input
                type="text"
                id="confirm-code-2"
                value={confirmCode2}
                onChange={handleConfirmCode2Change}
                className="w-full px-4 py-2 border border-gray-400 rounded"
              />
            </div>

            <div className="flex justify-between">
              <button
                onClick={handleConfirm}
                className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
              >
                Confirm Deal
              </button>
              <button
                onClick={handleFail}
                className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
              >
                Fail Deal
              </button>
            </div>
          </div>
        </div>
      );
}
