import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";
import Navbar from "../Helpers/Navbar";
import Footer from "../Helpers/Footer";

export default function ProductDetails() {
    const params = useParams();
    const noPhotoLink = 'https://st.depositphotos.com/2934765/53192/v/450/depositphotos_531920820-stock-illustration-photo-available-vector-icon-default.jpg';
    const [product, setProduct] = useState(null);
    const [photoLinks, setPhotoLinks] = useState([]);
    const [currentPhoto, setCurrentPhoto] = useState("");
    const [betAmount, setBetAmount] = useState("");
    const [showAllPhotos, setShowAllPhotos] = useState(false);

    useEffect(() => {
        let product_id = params.id;
        axios.get(`/products/${product_id}`)
            .then((res) => {
                let photos = res.data.product.photos;
                let main_image_url = res.data.product.main_image_url;
                if (main_image_url == "") {
                    if (!photos.length) {
                        setPhotoLinks([noPhotoLink]);
                    } else {
                        setPhotoLinks(photos);
                    }
                } else {
                    let temp_index = photos.indexOf(main_image_url);
                    let temp = photos[0];
                    photos[0] = main_image_url;
                    photos[temp_index] = temp;
                    setPhotoLinks(photos);
                }
                setCurrentPhoto(photos[0]);
                setProduct(res.data.product);
            })
    }, [params]);

    if (product) {
        if (showAllPhotos) {
           return (
               <>
                   <div id="default-modal" tabIndex="-1" aria-hidden="true" className="hidden overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 justify-center items-center w-full md:inset-0 h-[calc(100%-1rem)] max-h-full">
                       <div className="relative p-4 w-full max-w-2xl max-h-full">
                           <div className="relative bg-white rounded-lg shadow dark:bg-gray-700">
                               <div className="flex items-center justify-between p-4 md:p-5 border-b rounded-t dark:border-gray-600">
                                   <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                                       Terms of Service
                                   </h3>
                                   <button type="button" className="text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm w-8 h-8 ms-auto inline-flex justify-center items-center dark:hover:bg-gray-600 dark:hover:text-white" data-modal-hide="default-modal">
                                       <svg className="w-3 h-3" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 14">
                                           <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m1 1 6 6m0 0 6 6M7 7l6-6M7 7l-6 6"/>
                                       </svg>
                                       <span className="sr-only">Close modal</span>
                                   </button>
                               </div>
                               <div className="p-4 md:p-5 space-y-4">
                                   <p className="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                                       With less than a month to go before the European Union enacts new consumer privacy laws for its citizens, companies around the world are updating their terms of service agreements to comply.
                                   </p>
                                   <p className="text-base leading-relaxed text-gray-500 dark:text-gray-400">
                                       The European Union’s General Data Protection Regulation (G.D.P.R.) goes into effect on May 25 and is meant to ensure a common set of data rights in the European Union. It requires organizations to notify users as soon as possible of high-risk data breaches that could personally affect them.
                                   </p>
                               </div>
                               <div className="flex items-center p-4 md:p-5 border-t border-gray-200 rounded-b dark:border-gray-600">
                                   <button data-modal-hide="default-modal" type="button" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">I accept</button>
                                   <button data-modal-hide="default-modal" type="button" className="py-2.5 px-5 ms-3 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:ring-gray-100 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700">Decline</button>
                               </div>
                           </div>
                       </div>
                   </div>
               </>
           );
        }
        else {
            return (
                <>
                    <div className="mb-16">
                        <Navbar />
                    </div>

                    <div className="bg-gray-100 dark:bg-gray-800 py-8 w-2/4 mx-auto">
                        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
                            <div className="flex flex-col md:flex-row -mx-4">
                                <div className="md:flex-1 px-4">
                                    <div>

                                    </div>

                                    <div className="h-[460px] rounded-lg bg-gray-300 dark:bg-gray-700 mb-4">
                                        <img className="w-full h-full object-cover" onClick={() => setShowAllPhotos(true)} src={currentPhoto} alt=""/>
                                    </div>

                                    <div className="flex -mx-2 mb-4">
                                        <div className="w-1/2 px-2 mx-auto">
                                            <button
                                                className="w-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white py-2 px-4 rounded-full font-bold hover:bg-gray-300 dark:hover:bg-gray-600">
                                                Написать продавцу
                                            </button>
                                        </div>
                                    </div>
                                </div>
                                <div className="md:flex-1 px-4">
                                    <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-2">
                                        {product.name}
                                    </h2>
                                    <p className="text-gray-600 dark:text-gray-300 text-sm mb-4">
                                        Информация о владельце: {product.owner.first_name} {product.owner.second_name} <br/>
                                        Место
                                        продажи: {product.address_details.city.name}, {product.address_details.street} {product.address_details.building}
                                    </p>
                                    <div className="flex mb-4">
                                        <div className="mr-4">
                                        <span
                                            className="font-bold text-gray-700 dark:text-gray-300">Стартовая цена: </span>
                                            <span
                                                className="text-gray-600 dark:text-gray-300">{product.start_price} BYN</span>
                                        </div>
                                        <div>
                                        <span
                                            className="font-bold text-gray-700 dark:text-gray-300">Цена выкупа: </span>
                                            <span
                                                className="text-gray-600 dark:text-gray-300">{product.immediate_buy_price} BYN</span>
                                        </div>
                                    </div>
                                    <div className="mb-4">
                                    <span
                                        className="font-bold text-gray-700 dark:text-gray-300">Последняя ставка:</span>
                                        <div className="flex items-center mt-2 text-gray-50">
                                            30 BYN
                                        </div>
                                    </div>
                                    <div className="mb-4">
                                        <span className="font-bold text-gray-700 dark:text-gray-300">Категория:</span>
                                        <div className="flex items-center mt-2">
                                            <a href={`http://localhost:3000/categories/${product.category.id}`} className="bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-white py-2 px-4 rounded-full font-bold mr-2 hover:bg-gray-400 dark:hover:bg-gray-600">
                                                {product.category.category_name}
                                            </a>
                                        </div>
                                    </div>
                                    <div>
                                        <span className="font-bold text-gray-700 dark:text-gray-300">Описание:</span>
                                        <p className="text-gray-600 dark:text-gray-300 text-sm mt-2">
                                            {product.description}
                                        </p>
                                    </div>
                                    <div className="mt-4">
                                        <span className="font-bold text-gray-700 dark:text-gray-300">Ставка:</span>
                                        <div className="mt-2">
                                            <input type="text"
                                                   className="rounded-full"
                                                   value={betAmount}
                                                   onChange={(e) => setBetAmount(e.currentTarget.value)}
                                            />
                                            <span className="font-bold text-gray-700 dark:text-gray-300 ml-2">BYN</span>
                                        </div>
                                        <div className="w-1/2 px-2 mt-4 -ml-2">
                                            <button
                                                className="w-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white py-2 px-4 rounded-full font-bold hover:bg-gray-300 dark:hover:bg-gray-600">
                                                Сделать ставку
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="mt-16">
                        <Footer/>
                    </div>
                </>
            );
        }
    }
}
