import Image from "./Image";

export default function ProductImg({product, index = 0, className = null}) {
    if (!product.photos?.length && product.main_image_url === "") {
        return '';
    }
    if (!className) {
        className = 'h-3/5 w-full';
    }

    let photo = (product.main_image_url === "") ? product.photos[index] : product.main_image_url;
    return (
        <Image className={className} src={photo} alt="" />
    )
}
