import { useContext, useEffect } from "react";
import { useState } from "react";
import { UserContext } from "../UserContext";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io('http://localhost:8003');

export default function ChatsIndexPage() {
    const {user, getUser} = useContext(UserContext);
    const [chats, setChats] = useState(null);
    const [selectedChat, setSelectedChat] = useState(null);
    const [messages, setMessages] = useState(null);
    const [newMessage, setNewMessage] = useState(''); 

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            fetchChats();
            if (selectedChat) {
                fetchMessages(selectedChat.chat_id);
            }
            socket.on('chat', handleChatEvent);
            socket.on('message', handleMessageEvent);
            return () => {
                socket.off('chat', handleChatEvent);
                socket.off('message', handleMessageEvent);
            };
        }
    }, [user, chats, messages, selectedChat]);

    const handleChatEvent = (data) => {
        const { action, chat } = data;
        if (action === 'create') {
          setChats(prevChats => [...prevChats, chat]);
        }
    };

    const handleMessageEvent = (data) => {
        const { action, message } = data;
        if (action === 'create' && message.chat_id == selectedChat.chat_id) {
          setMessages(prevMessages => [...prevMessages, message]);
        }
    };

    const fetchChats = () => {
        axios.get(`http://localhost:8003/api/chats?user_id=${user.data.id}`)
          .then(response => {
            setChats(response.data);
          })
          .catch(error => {
            console.error('Error fetching chats:', error);
          });
    };

    const fetchMessages = (chatId) => {
        axios.get(`http://localhost:8003/api/chats/${chatId}/messages`)
          .then(response => {
            setMessages(response.data);
            socket.emit('join', chatId);
          })
          .catch(error => {
            console.error(`Error fetching messages for chat ${chatId}:`, error);
        });
    };


    const sendMessage = (chatId) => {
        if (newMessage.length > 0) {
            axios.post(`http://localhost:8003/api/chats/${chatId}/messages`, {
                message_text: newMessage,
                message_datetime: '2024-06-10 20:20:20',
                sender_id: user.data.id 
             })
             .then(() => {
               setNewMessage('');
             })
             .catch(error => {
               console.error('Error sending message:', error);
             });
        }
    };

    if (!chats) {
        return 'Pending...';
    }

    return (
      <div className="flex">
        <div className="w-1/4 h-screen border-r overflow-y-scroll">
          <h1 className="text-lg font-bold p-4">Чаты</h1>
          <ul>
            {chats.map(chat => (
              <li key={chat.chat_id} className="p-4 cursor-pointer" onClick={() => setSelectedChat(chat)}>
                {
                    chat.first_user.user_id == user.data.id
                    ?
                        <>
                          {chat.second_user.first_name} {chat.second_user.last_name}
                        </>
                    :
                        <>
                          {chat.first_user.first_name} {chat.first_user.last_name}
                        </>
                }
              </li>
            ))}
          </ul>
        </div>
        <div className="flex-1 p-4">
          {selectedChat && (
            <div className="flex flex-col h-screen">
              <h2 className="text-lg font-bold mb-4">
                  {
                      selectedChat.first_user.user_id == user.data.id
                      ?
                          <>
                            {selectedChat.second_user.first_name} {selectedChat.second_user.last_name}
                          </>
                      :   
                          <>
                            {selectedChat.first_user.first_name} {selectedChat.first_user.first_name}
                          </>    
                  }
              </h2>
              <div className="flex-1 border border-gray-300 p-4 overflow-y-scroll">
                {}
                {messages.map((message) => (
                  <div key={message.message_id} 
                    className={`mb-4 ${message.sender.user_id == user.data.id ? 'text-right' : 'text-left'}`}
                  >
                    <span className="inline-block bg-gray-200 rounded-lg px-3 py-1">{message.message_text}</span>
                  </div>
                ))}
              </div>
              <div className="flex p-2 gap-3">
                <input
                  type="text"
                  className="border border-gray-300 w-3/4"
                  placeholder="Type your message..."
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                />
                <button onClick={() => sendMessage(selectedChat.chat_id)} className="bg-blue-500 hover:bg-blue-600 text-white rounded w-1/4">
                  Send
                </button>
              </div>  
            </div>
          )}
        </div>
      </div>
    );
}
