import { useContext, useEffect } from "react";
import { useState } from "react";
import { UserContext } from "../UserContext";
import { io } from "socket.io-client";
import axios from "axios";

const socket = io('http://localhost:8003');

export default function ChatsIndexPage() {
    const {user, getUser} = useContext(UserContext);
    const [chats, setChats] = useState(null);
    const [selectedChat, setSelectedChat] = useState(null);
    const [messages, setMessages] = useState(null);
    const [newMessage, setNewMessage] = useState(''); 

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            fetchChats();
            if (selectedChat) {
                fetchMessages(selectedChat.chat_id);
            }
            socket.on('chat', handleChatEvent);
            socket.on('message', handleMessageEvent);
            return () => {
                socket.off('chat', handleChatEvent);
                socket.off('message', handleMessageEvent);
            };
        }
    }, [user, chats, messages, selectedChat]);

    const handleChatEvent = (data) => {
        const { action, chat } = data;
        if (action === 'create') {
          setChats(prevChats => [...prevChats, chat]);
        }
    };

    const handleMessageEvent = (data) => {
        const { action, message } = data;
        if (action === 'create' && message.chat_id == selectedChat.chat_id) {
          setMessages(prevMessages => [...prevMessages, message]);
        }
    };

    const fetchChats = () => {
        axios.get(`http://localhost:8003/api/chats?user_id=${user.data.id}`)
          .then(response => {
            setChats(response.data);
          })
          .catch(error => {
            console.error('Error fetching chats:', error);
          });
    };

    const fetchMessages = (chatId) => {
        axios.get(`http://localhost:8003/api/chats/${chatId}/messages`)
          .then(response => {
            setMessages(response.data);
            socket.emit('join', chatId);
          })
          .catch(error => {
            console.error(`Error fetching messages for chat ${chatId}:`, error);
        });
    };


    const sendMessage = (chatId) => {
        if (newMessage.length > 0) {
            axios.post(`http://localhost:8003/api/chats/${chatId}/messages`, {
                message_text: newMessage,
                message_datetime: '2024-06-10 20:20:20',
                sender_id: user.data.id 
             })
             .then(() => {
               setNewMessage('');
             })
             .catch(error => {
               console.error('Error sending message:', error);
             });
        }
    };

    return (
      <div className="flex bg-gray-100 min-h-screen">
        <div className="w-1/4 bg-white border-r overflow-y-scroll">
          <h1 className="text-xl font-bold py-4 px-6 border-b border-gray-200">Chats</h1>
          <ul>
            {chats.map(chat => (
              <li key={chat.id} className="px-6 py-4 cursor-pointer border-b border-gray-200 hover:bg-gray-50" onClick={() => setSelectedChat(chat)}>
                {chat.name}
              </li>
            ))}
          </ul>
        </div>
        <div className="flex-1 flex flex-col">
          {selectedChat && (
            <div className="flex flex-col flex-1">
              <div className="bg-white border-b border-gray-200 flex justify-between items-center p-4">
                <h2 className="text-lg font-bold">{selectedChat.name}</h2>
              </div>
              <div className="flex-1 bg-gray-50 p-4 overflow-y-scroll">
                {messages.map((message) => (
                  <div key={index} className={`mb-4 ${index % 2 === 0 ? 'text-left' : 'text-right'}`}>
                    <span className="inline-block bg-blue-500 text-white rounded-lg px-3 py-1 max-w-xs">{message}</span>
                  </div>
                ))}
              </div>
              <form onSubmit={sendMessage} className="p-4">
                <input
                  type="text"
                  className="border border-gray-300 p-3 w-full rounded"
                  placeholder="Type your message..."
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                />
                <button type="submit" className="mt-2 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded">
                  Send
                </button>
              </form>
            </div>
          )}
        </div>
      </div>
    );
}
