import Navbar from "../components/Navbar";
import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import Footer from "../components/Footer";

export default function IndexPage() {
    const params = useParams();
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get('/products/all')
            .then(res => {
                setProducts(/*res.data.data*/ []);
            })
    }, []);

    return (
        <div>
            <div className="bg-gray-100">
                {params.category_id && (
                    <Navbar selected_category_id={params.category_id}/>
                )}
                {!params.category_id && (
                    <Navbar/>
                )}

                {products.length && (
                    <div className="max-w-screen-2xl mx-auto p-5 sm:p-10 md:p-16">
                        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">
                            {products.map((product) => (
                                <ProductCard product={product}/>
                            ))}
                        </div>
                    </div>
                )}

                {!products.length && (
                    <h1 className="h-screen text-center text-4xl m-auto">Товары не найдены, измените параметры поиска</h1>

                )}

                <Footer />
            </div>
        </div>
    );
}
