import {Link} from "react-router-dom";
import {useContext, useEffect, useState} from "react";
import axios from "axios";
import {UserContext} from "../UserContext";
import ProductImg from "./ProductImg";

export default function ProfileSellings() {
    const [products, setProducts] = useState([]);
    const {user} = useContext(UserContext);

    useEffect(() => {
        axios.get('/products/?user_id=' + user.data.id)
            .then(res => {
                setProducts(res.data.data);
            })
            .catch(err => {
                console.log(err);
            })
    }, []);

    return (
        <div>
            <Link to={'/profile/sellings/new'} className="flex items-center justify-center">
                <button className="flex bg-gray-400 rounded-2xl px-4 py-2">
                    Добавить товар
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5"
                         stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round"
                              d="M12 9v6m3-3H9m12 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z"/>
                    </svg>
                </button>
            </Link>

            <div className="grid grid-cols-3 mt-12 ml-8 gap-2">
                {products.length > 0 && products.map(product => (
                    <div className="w-96 h-96 rounded bg-gray-800 overflow-hidden shadow-lg">
                        <ProductImg product={product}/>
                        <div className="px-6 py-4">
                            <div className="font-bold text-xl mb-2">{product.name}</div>
                            <p className="text-gray-700 text-base">
                                {product.description}
                            </p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
