import {Link} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";

export default function Navbar() {
    const [user, setUser] = useState(null);
    const [showCategories, setShowCategories] = useState(false);
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        axios.get('/main-page/')
            .then((res) => console.log(res.data.data))
            .catch(err => {
                console.log(err);
            })
    }, []);

    return (
        <div>
            <div className="bg-slate-800 flex items-center text-white h-16">
                <Link to={'/'} className="text-2xl hover:bg-gray-700 flex gap-2 items-center font-bold px-4">
                    Belbay
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5}
                         stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round"
                              d="M2.25 18.75a60.07 60.07 0 0 1 15.797 2.101c.727.198 1.453-.342 1.453-1.096V18.75M3.75 4.5v.75A.75.75 0 0 1 3 6h-.75m0 0v-.375c0-.621.504-1.125 1.125-1.125H20.25M2.25 6v9m18-10.5v.75c0 .414.336.75.75.75h.75m-1.5-1.5h.375c.621 0 1.125.504 1.125 1.125v9.75c0 .621-.504 1.125-1.125 1.125h-.375m1.5-1.5H21a.75.75 0 0 0-.75.75v.75m0 0H3.75m0 0h-.375a1.125 1.125 0 0 1-1.125-1.125V15m1.5 1.5v-.75A.75.75 0 0 0 3 15h-.75M15 10.5a3 3 0 1 1-6 0 3 3 0 0 1 6 0Zm3 0h.008v.008H18V10.5Zm-12 0h.008v.008H6V10.5Z"/>
                    </svg>
                </Link>

                <div className="relative ml-16">
                    <button className="flex gap-1 text-white hover:bg-gray-700 focus:outline-none"
                            onClick={() => setShowCategories(!showCategories)}>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5}
                             stroke="currentColor" className="w-6 h-6">
                            <path strokeLinecap="round" strokeLinejoin="round"
                                  d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5"/>
                        </svg>
                        Категории
                    </button>

                    <div className="absolute mt-2 bg-gray-800 text-white rounded-md shadow-lg">
                        {showCategories && categories.length > 0 && categories.map(category => (
                            <a href={"/" + category.category_name} className="block hover:bg-gray-700 px-4 py-2">{category.category_name}</a>
                        ))}
                    </div>
                </div>

                <div className="relative ml-24 flex">
                <input type="text" placeholder="Поиск по товарам"
                           className="border-2 border-gray-500 px-4 py-2 rounded-md focus:outline-none focus:border-blue-500"/>
                    <button className="relative right-0 top-0 mt-1 ml-2">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5}
                             stroke="currentColor" className="w-6 h-6">
                            <path strokeLinecap="round" strokeLinejoin="round"
                                  d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"/>
                        </svg>
                    </button>
                </div>

                {user && (
                    <div>

                    </div>
                )}

                {!user && (
                    <div className="ml-auto mr-8 grid grid-cols-2">
                        <Link to={'/login'}>
                        <button className="border hover:bg-gray-700 rounded-lg py-2 px-2">Логин</button>
                        </Link>
                        <Link to={'/register'}>
                            <button className="border hover:bg-gray-700 rounded-lg py-2 px-2 -ml-4">Регистрация</button>
                        </Link>
                    </div>
                )}
            </div>
        </div>
    );
}
