export default function DealCard({deal}) {
    return (
      <div className="max-w-lg rounded overflow-hidden shadow-lg m-4 bg-white transform transition duration-500 hover:scale-105 hover:shadow-2xl">
        <div className="px-8 py-6">
          <div className="font-bold text-2xl mb-2 text-gray-800">{deal.bet.product.product_name}</div>
          <p className="text-gray-700 text-base mb-4">
            {deal.bet.price}
          </p>
        </div>
        <div className="px-8 pb-6">
          <span className={`inline-block ${deal.deal_status.status_name === 'CLOSED_SUCCESFULLY' ? 'bg-green-500' : deal.deal_status.status_name === 'STARTED' ? 'bg-red-500' : 'bg-yellow-500'} rounded-full px-3 py-1 text-sm font-semibold text-white`}>
            {deal.deal_status.status_description}
          </span>
        </div>
      </div>
    );
}
