import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Footer from "../../Helpers/Footer";

export default function ProfileSellingBets() {
    const params = useParams();
    const [bets, setBets] = useState([]);

    useEffect(() => {
        let product_id = params.action;
        if (product_id) {
            axios.get(`/bets/list-product-bets/${product_id}`)
                .then(res => {
                    setBets([]);
                });
        }
    }, [params]);

    if (bets) {
        return (
            <div className="rounded-lg shadow-md overflow-hidden">
                <ul className="mt-32 mb-48">
                    {bets.length && bets.map(bet => (
                        <>
                        <li className="flex px-4 py-3 rounded-xl bg-slate-800 border-b border-gray-200">
                            <span className="text-white mr-2">
                                Покупатель: {bet.user.first_name} {bet.user.second_name}, рейтинг - {bet.user.buyer_rating.rating}
                            </span>
                            <span className="text-white m-auto">Ставка: {bet.price} BYN, дата ставки - {bet.made_datetime}</span>

                            {bet.status == 'сделана' && (
                                <div className="text-sm text-blue-500 font-bold">
                                    <button className="hover:bg-blue-500 hover:rounded-xl hover:text-white h-8 px-4">Принять</button>
                                    <button className="hover:bg-red-500 hover:rounded-xl hover:text-white h-8 px-4">Отклонить</button>
                                </div>
                            )}

                        </li>
                        <br />
                        </>
                    ))}
                </ul>

                {bets.length && (
                    <Footer />
                )}    

                {!bets.length && (
                    <div className="mt-64">
                        <Footer />
                    </div>
                )}
            </div>
        );
    }
}
