import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Footer from "../../Helpers/Footer";

export default function ProfileSellingBets() {
    const params = useParams();
    const [bets, setBets] = useState([]);

    useEffect(() => {
        let product_id = params.action;
        if (product_id) {
            axios.get(`/bets/list-product-bets/${product_id}`)
                .then(res => {
                    setBets(res.data.betsData.bets);
                });
        }
    }, [params]);

    if (bets) {
        return (
            <div>
                {bets.length 
                    ? 
                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4">
                    {bets.map((bet) => (
                        <div className="max-w-xs mx-auto bg-white shadow-lg rounded-lg overflow-hidden m-4">
                            <div className="px-4 py-2">
                            <h2 className="text-gray-800 font-bold text-xl mb-2">Ставка от пользователя {bet.user.first_name} {bet.user.second_name}</h2>
                            <p className="text-gray-600 text-sm">Стоимость: {bet.price} BYN</p>
                            <p className="text-gray-600 text-sm">Рейтинг покупателя: {bet.user.buyer_rating.rating}</p>
                            <p className="text-gray-600 text-sm">Дата ставки: {bet.made_datetime}</p>
                            <p className="text-gray-600 text-sm">Статус: {bet.status}</p>
                            </div>
                            {bet.status == "сделана" && (
                                <div className="px-4 py-2 flex justify-between">
                                    <button
                                        onClick={() => console.log('accept')}
                                        className="px-4 py-2 bg-green-500 text-white font-semibold rounded hover:rounded-lg"
                                    >
                                        Принять
                                    </button>
                                    <button
                                        onClick={() => console.log('decline')}
                                        className="px-4 py-2 bg-red-500 text-white font-semibold rounded"
                                    >
                                        Отклонить
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                    </div>
                    :
                    <>
                    </>
                }

                {bets.length 
                    ?
                    <Footer />
                    :
                    <div>
                        <h2 className="text-center text-2xl">Ставки не найдены</h2>
                        <br/>
                        <br/>
                        <div className="mt-96">
                            <Footer />
                        </div>    
                    </div>
                }
            </div>
        );
    }
}
