import Image from "./Image";

export default function ProductImg({product, index = 0, className = null}) {
    if (!product.main_image_url && !product.photos) {
        return '';
    }

    let photoUrl = product.main_image_url;
    if (!photoUrl) {
        photoUrl = product.photos[index];
    }

    if (!className) {
        className = 'h-3/5 w-full';
    }
    return (
        <Image className={className} src={photoUrl} alt="" />
    )
}
