import {Link} from "react-router-dom";
import {useContext, useEffect, useState} from "react";
import axios from "axios";
import {UserContext} from "../UserContext";
import ProductCard from "./ProductCard";
import Footer from "./Footer";

export default function ProfileSellings() {
    const [products, setProducts] = useState([]);
    const {user} = useContext(UserContext);
    const [deleteButtonText, setDeleteButtonText] = useState('Удалить товары');
    const [deleteButtonClassName, setDeleteButtonClassName] = useState('flex bg-gray-400 ml-2 rounded-2xl px-4 py-2');
    const [chosenProducts, setChosenProduct] = useState([]);

    useEffect(() => {
        axios.get('/products/?user_id=' + user.data.id)
            .then(res => {
                setProducts(res.data.data);
            })
            .catch(err => {
                console.log(err);
            })
    }, []);

    function deleteProducts() {

    }

    return (
        <div>
            <div className="flex items-center justify-center">
                <Link to={'/profile/sellings/new'} className="flex bg-gray-400 rounded-2xl px-4 py-2">
                    Добавить товар
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5"
                         stroke="currentColor" className="w-6 h-6 ml-2">
                        <path strokeLinecap="round" strokeLinejoin="round"
                              d="M12 9v6m3-3H9m12 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z"/>
                    </svg>
                </Link>
                <button onClick={deleteProducts} className={deleteButtonClassName}>
                    {deleteButtonText}
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5}
                         stroke="currentColor" className="w-6 h-6 ml-2">
                        <path strokeLinecap="round" strokeLinejoin="round"
                              d="M15 12H9m12 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z"/>
                    </svg>
                </button>
            </div>

            {products.length &&
                <div className="max-w-screen-2xl mx-auto p-5 sm:p-10 md:p-16">

                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">

                    {products.map((product) => (
                            <ProductCard product={product}/>
                        ))}

                    </div>

                </div>
            }
            {!products.length &&
                <h2 className="text-black">OZERA</h2>
            }

            <Footer/>
        </div>
    );
}
