import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";
import Navbar from "../Helpers/Navbar";
import Footer from "../Helpers/Footer";

export default function ProductDetails() {
    const params = useParams();
    const noPhotoLink = 'https://st.depositphotos.com/2934765/53192/v/450/depositphotos_531920820-stock-illustration-photo-available-vector-icon-default.jpg';
    const [product, setProduct] = useState(null);
    const [photoLinks, setPhotoLinks] = useState([]);
    const [currentPhoto, setCurrentPhoto] = useState("");

    useEffect(() => {
        let product_id = params.id;
        axios.get(`/products/${product_id}`)
            .then((res) => {
                let photos = res.data.product.photos;
                let main_image_url = res.data.product.main_image_url;
                if (main_image_url == "") {
                    if (!photos.length) {
                        setPhotoLinks([noPhotoLink]);
                    } else {
                        setPhotoLinks(photos);
                    }
                } else {
                    let temp_index = photos.indexOf(main_image_url);
                    let temp = photos[0];
                    photos[0] = main_image_url;
                    photos[temp_index] = temp;
                    setPhotoLinks(photos);
                }
                setCurrentPhoto(photos[0]);
                setProduct(res.data.product);
            })
    }, [params]);

    if (product) {
        return (
            <>
                <div className="mb-16">
                    <Navbar />
                </div>

                <div className="bg-gray-100 dark:bg-gray-800 py-8 w-2/4 mx-auto">
                    <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
                        <div className="flex flex-col md:flex-row -mx-4">
                            <div className="md:flex-1 px-4">
                                <div className="h-[460px] rounded-lg bg-gray-300 dark:bg-gray-700 mb-4">
                                    <img className="w-full h-full object-cover" src={currentPhoto}  alt=""/>
                                </div>
                                <div className="flex -mx-2 mb-4">
                                    <div className="w-1/2 px-2">
                                        <button className="w-full bg-gray-900 dark:bg-gray-600 text-white py-2 px-4 rounded-full font-bold hover:bg-gray-800 dark:hover:bg-gray-700">Add to Cart</button>
                                    </div>
                                    <div className="w-1/2 px-2">
                                        <button className="w-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white py-2 px-4 rounded-full font-bold hover:bg-gray-300 dark:hover:bg-gray-600">Add to Wishlist</button>
                                    </div>
                                </div>
                            </div>
                            <div className="md:flex-1 px-4">
                                <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-2">
                                    {product.name}
                                </h2>
                                <p className="text-gray-600 dark:text-gray-300 text-sm mb-4">
                                    Информация о владельце: {product.owner.first_name} {product.owner.second_name} <br />
                                    Место продажи: {product.address_details.city.name}, {product.address_details.street} {product.address_details.building}
                                </p>
                                <div className="flex mb-4">
                                    <div className="mr-4">
                                        <span className="font-bold text-gray-700 dark:text-gray-300">Стартовая цена: </span>
                                        <span className="text-gray-600 dark:text-gray-300">{product.start_price} BYN</span>
                                    </div>
                                    <div>
                                        <span className="font-bold text-gray-700 dark:text-gray-300">Цена выкупа: </span>
                                        <span className="text-gray-600 dark:text-gray-300">{product.immediate_buy_price} BYN</span>
                                    </div>
                                </div>
                                <div className="mb-4">
                                    <span className="font-bold text-gray-700 dark:text-gray-300">Последняя ставка:</span>
                                    <div className="flex items-center mt-2 text-gray-50">
                                        30 BYN
                                    </div>
                                </div>
                                <div className="mb-4">
                                    <span className="font-bold text-gray-700 dark:text-gray-300">Select Size:</span>
                                    <div className="flex items-center mt-2">
                                        <button className="bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-white py-2 px-4 rounded-full font-bold mr-2 hover:bg-gray-400 dark:hover:bg-gray-600">S</button>
                                        <button className="bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-white py-2 px-4 rounded-full font-bold mr-2 hover:bg-gray-400 dark:hover:bg-gray-600">M</button>
                                        <button className="bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-white py-2 px-4 rounded-full font-bold mr-2 hover:bg-gray-400 dark:hover:bg-gray-600">L</button>
                                        <button className="bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-white py-2 px-4 rounded-full font-bold mr-2 hover:bg-gray-400 dark:hover:bg-gray-600">XL</button>
                                        <button className="bg-gray-300 dark:bg-gray-700 text-gray-700 dark:text-white py-2 px-4 rounded-full font-bold mr-2 hover:bg-gray-400 dark:hover:bg-gray-600">XXL</button>
                                    </div>
                                </div>
                                <div>
                                    <span className="font-bold text-gray-700 dark:text-gray-300">Product Description:</span>
                                    <p className="text-gray-600 dark:text-gray-300 text-sm mt-2">
                                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
                                        sed ante justo. Integer euismod libero id mauris malesuada tincidunt. Vivamus commodo nulla ut
                                        lorem rhoncus aliquet. Duis dapibus augue vel ipsum pretium, et venenatis sem blandit. Quisque
                                        ut erat vitae nisi ultrices placerat non eget velit. Integer ornare mi sed ipsum lacinia, non
                                        sagittis mauris blandit. Morbi fermentum libero vel nisl suscipit, nec tincidunt mi consectetur.
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="mt-16">
                    <Footer/>
                </div>
            </>
        );
    }
}
