import axios from "axios";
import Echo from "laravel-echo";
import Pusher from "pusher-js";
import { useContext, useEffect } from "react";
import { UserContext } from "../UserContext";
import { useState } from "react";
import { Navigate, useParams } from "react-router-dom";

export default function ChatsIndexPage() {
    const [chats, setChats] = useState([]);
    const params = useParams();
    const {user, getUser} = useContext(UserContext);
    const [selectedChat, setSelectedChat] = useState(null);
    const [messages, setMessages] = useState([]);
    const [redirect, setRedirect] = useState('');
    const [newMessage, setNewMessage] = useState('');

    useEffect(() => {
        if (!user) {
            getUser();
        } else {
            window.Pusher = Pusher;
            window.Echo = new Echo({
                broadcaster: 'pusher',
                key: '0abcc2e1499f2ddde046',
                cluster: 'eu',
                encrypted: true
            })

            console.log(window);

            const fetchChats = async () => {
                const response = await axios.get(`/chats?user_id=${user.data.id}`);
                setChats(response.data);
            }

            fetchChats();
        }
    }, [user]);

    useEffect(() => {
        if (selectedChat) {
            console.log('fetch message');
            window.Echo.private(`chat.${selectedChat.chat_id}`)
            .listen('MessageSent', (e) => {
                console.log(e);
                setMessages(messages => [...messages, e.message]);
            });

            return () => {
                window.Echo.leaveChannel(`chat.${selectedChat.chat_id}`);
            }
        }    
    }, [user]);

    const selectChat = async (chat_id) => {
        const response = await axios.get(`/chats/${chat_id}`);
        setSelectedChat(response.data);
        setMessages(response.data.messages);
    };

    const sendMessage = async () => {
        if (selectedChat && newMessage.length != null) {
            var today = new Date();
            await axios.post(`/chats/${selectedChat.chat_id}`, {
                message_text: newMessage,
                sender_id: user.data.id,
                chat_id: selectedChat.chat_id,
                message_datetime: `${today.getFullYear()}-${today.getMonth() + 1}-${today.getDate()} 
                    ${today.getHours()}:${today.getMinutes()}:${today.getSeconds()}`
            });

            setNewMessage('');
        }
    };

    if (redirect) {
        return <Navigate to={redirect}/>
    }

    return (
        <div>
            <div>
                <h2>Chat Rooms</h2>
                <ul>
                    {chats.map(chat => (
                        <li key={chat.chat_id} onClick={() => setRedirect()}>
                            {chat.chat_id}
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                {selectChat && (
                    <>
                        <h3>Messages in {selectChat.chat_id}</h3>
                        <div>
                            {messages.map((msg) => (
                                <div key={msg.message_id}>{msg.message_text}</div>
                            ))}
                        </div>
                        <input
                            type="text"
                            value={newMessage}
                            onChange={(e) => setNewMessage(e.target.value)}
                        />
                        <button onClick={sendMessage}>Send</button>
                    </>
                )}
            </div>
        </div>
    );
}
